import React, { useState } from 'react';
import './TaskTable.css'; // You can define your CSS for styling
import TimePicker from 'react-time-picker';

function TaskTable() {
  const initialRows = Array(5).fill({ time: '', task: '', goal: '' });

  const [rows, setRows] = useState(initialRows);

  const handleRowChange = (index, field, value) => {
    const updatedRows = [...rows];
    updatedRows[index] = {
      ...updatedRows[index],
      [field]: value,
    };
    setRows(updatedRows);
  };

  const handleAddRow = () => {
    setRows([...rows, { time: '', task: '', goal: '' }]);
  };

  const handleDeleteRow = (index) => {
    const updatedRows = [...rows];
    updatedRows.splice(index, 1);
    setRows(updatedRows);
  };

  return (
    <div>
      <div className="table-container">
        <table className="task-table">
          <thead>
            <tr>
              <th>Time</th>
              <th>Task</th>
              <th>Goal</th>
              <th>Action</th>
            </tr>
          </thead>
          <tbody>
            {rows.map((row, index) => (
              <tr key={index}>
                <td>
                <TimePicker
                    type="text"
                    value={row.time}
                    onChange={(e) => handleRowChange(index, 'time', e.target.value)} />
                </td>
                <td>
                  <input
                    type="text"
                    value={row.task}
                    onChange={(e) => handleRowChange(index, 'task', e.target.value)}
                  />
                </td>
                <td>
                  <input
                    type="text"
                    value={row.goal}
                    onChange={(e) => handleRowChange(index, 'goal', e.target.value)}
                  />
                </td>
                <td>
                  <button onClick={() => handleDeleteRow(index)}>Delete</button>
                </td>
              </tr>
            ))}
          </tbody>
        </table>
      </div>
      <button onClick={handleAddRow}>Add Row</button>
    </div>
  );
}

export default TaskTable;
