import React, { useState } from "react";

function Timer() {
  const [minutes, setMinutes] = useState(0);
  const [seconds, setSeconds] = useState(5);
  const [play, setPlay] = useState(false);
  const [breaks, setBreaks] = useState(false);
  const [heading, setHeading] = useState("Work");
  const [showOptions, setShowOptions] = useState(false);
  const [selectedInitialTime, setSelectedInitialTime] = useState(25);

  const handlePlayPause = () => {
    setPlay(!play);
  };

  const handleToggleSession = () => {
    setPlay(false);
    setBreaks(!breaks);

    if (!breaks) {
      setMinutes(selectedInitialTime);
      setHeading("Break");
    } else {
      setMinutes(selectedInitialTime);
      setHeading("Work");
    }
  };

  const handleOptionsClick = () => {
    setShowOptions(!showOptions);
  };

  const handleInitialTimeSelect = (time) => {
    setSelectedInitialTime(time);
    setShowOptions(false);
  };

  return (
    <div style={timerContainerStyle}>
      <h2 style={timerTextStyle}>
        {heading}: {minutes} : {seconds < 10 ? `0${seconds}` : seconds}
      </h2>
      <div style={optionsContainerStyle}>
        <button style={optionsButtonStyle} onClick={handleOptionsClick}>
          ⋮
        </button>
        {showOptions && (
          <div style={optionsDropdownStyle}>
            <p style={optionsTitleStyle}>Select Initial Time</p>
            <button
              style={optionsButtonStyle}
              onClick={() => handleInitialTimeSelect(25)}
            >
              25 minutes
            </button>
            <button
              style={optionsButtonStyle}
              onClick={() => handleInitialTimeSelect(45)}
            >
              45 minutes
            </button>
            {/* Add more initial time options as needed */}
          </div>
        )}
      </div>
      <button style={playPauseButtonStyle} onClick={handlePlayPause}>
        {play ? "⏸️" : "▶️"}
      </button>
      <button style={toggleButtonStyle} onClick={handleToggleSession}>
        Toggle Session
      </button>
    </div>
  );
}

const timerContainerStyle = {
  textAlign: "center",
  margin: "20px",
  padding: "10px",
  backgroundColor: "#f0f0f0",
  borderRadius: "8px",
  boxShadow: "0 4px 8px rgba(0, 0, 0, 0.1)",
};

const timerTextStyle = {
  fontSize: "24px",
  color: "#333",
};

const optionsContainerStyle = {
  display: "inline-block",
  position: "relative",
};

const optionsButtonStyle = {
  fontSize: "18px",
  cursor: "pointer",
  margin: "10px",
};

const optionsDropdownStyle = {
  position: "absolute",
  top: "30px",
  right: "0",
  backgroundColor: "#fff",
  borderRadius: "8px",
  boxShadow: "0 4px 8px rgba(0, 0, 0, 0.1)",
  padding: "10px",
  textAlign: "center",
};

const optionsTitleStyle = {
  fontSize: "16px",
  fontWeight: "bold",
  marginBottom: "10px",
};

const playPauseButtonStyle = {
  fontSize: "24px",
  marginLeft: "10px",
  cursor: "pointer",
};

const toggleButtonStyle = {
  fontSize: "18px",
  margin: "10px",
  cursor: "pointer",
};

export default Timer;
