import React from 'react';
import './TimerOptions.css';
import { useState } from 'react';

const WorkDurationOptions = ({ top, left, onClose, play, setMinutes, setSeconds, setWorkDuration, breaks, started, setShowOptions }) => {


  const [firstClicked, setFirstClicked] = useState(false)
  const [secClicked, setSecClicked] = useState(false)
  const [thirdClicked, setThirdClicked] = useState(false)
  const [fourthClicked, setFourthClicked] = useState(true)
  const [fifthClicked, setFifthClicked] = useState(false)
  const [sixthClicked, setSixthClicked] = useState(false)
  const [sevClicked, setSevClicked] = useState(false)
  const [eightClicked, setEightClicked] = useState(false)
  const [ninthClicked, setNinthClicked] = useState(false)

  function handleTimeChange(min, sec, setClicked){
    if(!breaks && !play && !started){
        setMinutes(min)
        setSeconds(sec)
    }
    if(firstClicked){
      setFirstClicked(false)
    }
    if(secClicked){
      setSecClicked(false)
    }
    if(thirdClicked){
      setThirdClicked(false)
    }
    if(fourthClicked){
      setFourthClicked(false)
    }
    if(fifthClicked){
      setFifthClicked(false)
    }
    if(sixthClicked){
      setSixthClicked(false)
    }
    if(sevClicked){
      setSevClicked(false)
    }
    if(eightClicked){
      setEightClicked(false)
    }
    if(ninthClicked){
      setNinthClicked(false)
    }
    setClicked(true)
    setWorkDuration(min)
    setShowOptions(false)
  }
  return (
    <div className="options-view" style={{ top, left }}>
      <ul>
       <li className="solid-white-background" onClick = {()=>handleTimeChange(0, 2, setFirstClicked)}> {firstClicked && '√'} 2 sec</li>
        <li className="solid-white-background"  onClick = {()=>handleTimeChange(0, 15, setSecClicked)}> {secClicked && '√'}  15 minutes</li> 
         <li className="solid-white-background" onClick = {()=>handleTimeChange(20, 0, setThirdClicked)}>  {thirdClicked && '√'} 20 minutes </li>
         <li className="solid-white-background" onClick = {()=>handleTimeChange(25, 0, setFourthClicked)}>  {fourthClicked && '√'} 25 minutes </li>
         <li className="solid-white-background" onClick = {()=>handleTimeChange(30, 0, setFifthClicked)}> {fifthClicked && '√'} 30 minutes </li>
        <li className="solid-white-background" onClick = {()=>handleTimeChange(45, 0, setSixthClicked)}> {sixthClicked && '√'} 45 minutes</li> 
         <li className="solid-white-background" onClick = {()=>handleTimeChange(60, 0, setSevClicked)}> {sevClicked &&'√'} 60 minutes </li> 
        <li className="solid-white-background" onClick = {()=>handleTimeChange(75, 0, setEightClicked)}> {eightClicked && '√' } 75 minutes </li> 
      <li className="solid-white-background" onClick = {()=>handleTimeChange(90, 0, setNinthClicked)}>  {ninthClicked && '√'}  90 minutes </li> 
      <li className="solid-white-background" onClick={onClose}>Close</li>
      </ul>
    </div>
  );
};

export default WorkDurationOptions;
