import React, { useState } from 'react';
import './TimerOptions.css';
import WorkDurationOptions from './WorkDurationOptions';
import BreakDurationOptions from './BreakDuration';
const TimerOptions = ({ top, left, onClose, play, setMinutes, setSeconds, setWorkDuration }) => {
  const [workHover, setWorkHover] = useState(false);

  return (
    <div className="options-view" style={{ top, left }}>
      <ul>
        <li onMouseEnter={() => setWorkHover(true)} onMouseLeave={() => setWorkHover(false)}>
          Set Work duration

        </li>
        {workHover && <WorkDurationOptions className="work-duration-options" play = {play} setMinutes = {setMinutes} setSeconds = {setSeconds} setWorkDuration = {setWorkDuration}/>}
        <li>Set Break duration</li>
        {workHover && <BreakDurationOptions className="work-duration-options" play = {play} setMinutes = {setMinutes} setSeconds = {setSeconds} setBreakDuration = {setBreakDuration}/>}
        <li>Set Alarm Sound</li>
        <li>Hide Timer</li>
        <li>Reset Timer</li>
        <li>Start Work Timer Automatically</li>
        <li>Start Break Timer Automatically</li>
        <li onClick={onClose}>Close</li>
      </ul>
    </div>
  );
};

export default TimerOptions;
