import React, { useState, useEffect } from 'react';
import './TimerOptions.css';

const WorkDurationOptions = ({ top, left, onClose, play, setMinutes, setSeconds, setWorkDuration, breaks, started, setShowOptions }) => {
  const [selectedOption, setSelectedOption] = useState(25); // Set the default selected option

  useEffect(() => {
    // Set the default selected option when the component mounts
    setWorkDuration(selectedOption);
  }, [setWorkDuration, selectedOption]);

  function handleTimeChange(min, sec) {
    if (!breaks && !play && !started) {
      setMinutes(min);
      setSeconds(sec);
    }

    setWorkDuration(min);
    setSelectedOption(min); // Update the selected option
    setShowOptions(false);
  }

  return (
    <div className="options-view" style={{ top, left }}>
      <ul>
        <li className={`solid-white-background ${selectedOption === 2 ? 'selected' : ''}`} onClick={() => handleTimeChange(0, 2)}>
          2 sec
        </li>
        <li className={`solid-white-background ${selectedOption === 15 ? 'selected' : ''}`} onClick={() => handleTimeChange(0, 15)}>
          15 minutes
        </li>
        <li className={`solid-white-background ${selectedOption === 20 ? 'selected' : ''}`} onClick={() => handleTimeChange(20, 0)}>
          20 minutes
        </li>
        <li className={`solid-white-background ${selectedOption === 25 ? 'selected' : ''}`} onClick={() => handleTimeChange(25, 0)}>
          25 minutes
        </li>
        <li className={`solid-white-background ${selectedOption === 30 ? 'selected' : ''}`} onClick={() => handleTimeChange(30, 0)}>
          30 minutes
        </li>
        <li className={`solid-white-background ${selectedOption === 45 ? 'selected' : ''}`} onClick={() => handleTimeChange(45, 0)}>
          45 minutes
        </li>
        <li className={`solid-white-background ${selectedOption === 60 ? 'selected' : ''}`} onClick={() => handleTimeChange(60, 0)}>
          60 minutes
        </li>
        <li className={`solid-white-background ${selectedOption === 75 ? 'selected' : ''}`} onClick={() => handleTimeChange(75, 0)}>
          75 minutes
        </li>
        <li className={`solid-white-background ${selectedOption === 90 ? 'selected' : ''}`} onClick={() => handleTimeChange(90, 0)}>
          90 minutes
        </li>
        <li className={`solid-white-background ${selectedOption === 25 ? 'selected' : ''}`} onClick={onClose}>
          Close
        </li>
      </ul>
    </div>
  );
};

export default WorkDurationOptions;
