import React, {useState} from "react";
import './CreateAccount.css'
import { useNavigate } from 'react-router-dom';
import axios from 'axios';

function CreateAccount(){

    const navigation = useNavigate();
    const [name, setName] = useState('');
    const [email, setEmail] = useState('');
    const [phoneNumber, setPhoneNumber] = useState('');
    const [username, setUsername] = useState('');
    const [password, setPassword] = useState('');
    const [repassword, setRepassword] = useState('');
    const [message, setMessage] = useState('');
    const [passwordMessage, setPasswordMessage] = useState('')
    const URL = "http://localhost:3000";

    function handleRepeatChange(e){
      setRepassword(e.target.value)
      if(password && e.target.value && e.target.value !== password){
        setPasswordMessage("Passwords don't match!")
      }
      else{
        setPasswordMessage("")
      }
    }

    async function handleLogin(e) {
      e.preventDefault();
      if(!name || !username || !password || !email || !phoneNumber || password != repassword){
        setMessage("Not all entries filled")
      }
      else{


        try{

          const data = {
              name: name,
              username: username,
              password: password,
              email: email,
              phoneNumber: phoneNumber,
          }
          const response = await axios.post(URL + "/user", data); 
          console.log(response.status)
          console.log('Response from the server:', response.data);
          navigation("/createTable", {
              state: {
                  username: username
                },
          });

        }
        catch(error){
          console.log(error)
        }
      }

    }


    return (
        <div className="login-container">

      <form className="login-form" onSubmit={handleLogin}>
        <h1>Create Account </h1>
        <label htmlFor="Name">Name :</label>
        <input
          type="text"
          id="name"
          value={name}
          onChange={(e) => setName(e.target.value)}
        />
        <label htmlFor="email"> Enter Email:</label>
        <input
          type="text"
          id="email"
          value={email}
          onChange={(e) => setEmail(e.target.value)}
        />

        <label htmlFor="phoneNumber"> Enter Phone Number:</label>
        <input
          type="text"
          id="phoneNumber"
          value={phoneNumber}
          onChange={(e) => setPhoneNumber(e.target.value)}
        />
         <label htmlFor="username"> Create Username:</label>
        <input
          type="text"
          id="username"
          value={username}
          onChange={(e) => setUsername(e.target.value)}
        />
        <label htmlFor="password"> Create Password:</label>
        <input
          type="password"
          id="password"
          value={password}
          onChange={(e) => setPassword(e.target.value)}
        />
         <label htmlFor="password"> Reenter Password:</label>
        <input
          type="password"
          id="password"
          value={repassword}
          onChange={handleRepeatChange}
        />
        <p className="message"> {passwordMessage} </p>
        <button className='CreateAccountButton' type="submit">Create Account</button>
        <a onClick = {()=> navigation("/LogIn")}> Already Registered? Click here </a>
        <p className="message">{message}</p>
      </form>
    </div>
    )

}

export default CreateAccount
