import React, { useState, useEffect } from 'react';
import './TimerOptions.css';

const WorkDurationOptions = ({ top, left, onClose, play, setMinutes, setSeconds, setWorkDuration, breaks, started, setShowOptions }) => {
  const [selectedOption, setSelectedOption] = useState(25); // Set the default selected option

  useEffect(() => {
    // Set the default selected option when the component mounts
    setWorkDuration(selectedOption);
  }, [setWorkDuration, selectedOption]);

  function handleTimeChange(min, sec) {
    if (!breaks && !play && !started) {
      setMinutes(min);
      setSeconds(sec);
    }

    setWorkDuration(min);
    setSelectedOption(min); // Update the selected option
    setShowOptions(false);
  }

  return (
    <div className="options-view" style={{ top, left }}>
      <ul>
        <li className={`work-option ${selectedOption === 2 ? 'selected' : ''}`} onClick={() => handleTimeChange(0, 2)}>
          {selectedOption === 2 && <span className="checkmark">&#10004;</span>} 2 sec
        </li>
        <li className={`work-option ${selectedOption === 15 ? 'selected' : ''}`} onClick={() => handleTimeChange(0, 15)}>
          {selectedOption === 15 && <span className="checkmark">&#10004;</span>} 15 minutes
        </li>
        {/* Repeat the pattern for other options */}
        <li className={`work-option ${selectedOption === 25 ? 'selected' : ''}`} onClick={() => handleTimeChange(25, 0)}>
          {selectedOption === 25 && <span className="checkmark">&#10004;</span>} 25 minutes
        </li>
        {/* Add checkmark for other options */}
        <li className={`work-option ${selectedOption === 30 ? 'selected' : ''}`} onClick={() => handleTimeChange(30, 0)}>
          {selectedOption === 30 && <span className="checkmark">&#10004;</span>} 30 minutes
        </li>
        {/* Repeat for other options */}
        <li className={`work-option ${selectedOption === 90 ? 'selected' : ''}`} onClick={() => handleTimeChange(90, 0)}>
          {selectedOption === 90 && <span className="checkmark">&#10004;</span>} 90 minutes
        </li>
        <li className={`work-option ${selectedOption === 25 ? 'selected' : ''}`} onClick={onClose}>
          {selectedOption === 25 && <span className="checkmark">&#10004;</span>} Close
        </li>
      </ul>
    </div>
  );
};

export default WorkDurationOptions;
